/* 4) Create a Director class inside a module and set it as a dependency on the Movie module. */
function Director(aName, aQuotes) {
  /* 6) Add name:string, a quotes:array properties, and a speak() method to Director; calling speak() will return directorâ€™s quotes. */
  this.name = aName;
  this.quotes = aQuotes !== undefined ? aQuotes : [];
  /* aQuotes is an optional parameter of the constructor.
  If it's absent (undefined) the "quotes" propery of the object
  is declared as en empty array */

  this.speak = function() {
    console.log(this.name + " says: \n* \"" + this.quotes.join("\"\n* \"") + "\"");
  }
}


/* --- Module Exports --- */

module.exports = Director;